mapper.insert:
insert into board_admin(
                        category_name
                       , category_number
                       , contents
                       , board_file_count
                       , board_file_yn
                       , reply_yn
                       , comments_yn
                       , create_time
                       , update_time
)values(
                        #{categoryName}
                       , #{categoryNumber}
                       , #{contents}
                       , #{boardFileCount}
                       , #{boardFileYn}
                       , #{replyYn}
                       , #{commentsYn}
                       , #{createTime}
                       , #{updateTime}
)

mapper.select:
select id , category_name , category_number , contents , board_file_yn , board_file_count , reply_yn , comments_yn , create_time , update_time , del_yn
from board_admin
where del_yn = 'N' and category_number = #{categoryNumber}

mapper.update:
update  board_admin
set
        category_name = #{categoryName}
        , contents = #{contents}
        , board_file_count = #{boardFileCount}
        , board_file_yn = #{boardFileYn}
        , reply_yn = #{replyYn}
        , comments_yn = #{commentsYn}
        , update_time = #{updateTime}

where
        del_yn = 'N' and id = #{id}
