mapper.insert:
insert into board_admin(
                        category_name
                       , category_number
                       , contents
                       , attached_file_count
                       , attached_file_yn
                       , reply_yn
                       , comments_yn
                       , create_time
                       , update_time
)values(
        #{categoryName}
       , #{categoryNumber}
       , #{contents}
       , #{attachedFileCount}
       , #{attachedFileYn}
       , #{replyYn}
       , #{commentsYn}
       , #{createTime}
       , #{updateTime}
       )

mapper.select:
select id , category_name , category_number , contents , attached_file_yn , attached_file_count , reply_yn , comments_yn , create_time , update_time , del_yn
from board_admin
where del_yn = 'N' and category_number = #{categoryNumber}

mapper.update:
update  board_admin
set
    category_name = #{categoryName}
  , contents = #{contents}
  , attached_file_count = #{attachedFileCount}
  , attached_file_yn = #{attachedFileYn}
  , reply_yn = #{replyYn}
  , comments_yn = #{commentsYn}
  , update_time = #{updateTime}

where
    del_yn = 'N' and id = #{id}
