mapper.select:
SELECT b.id, (@rownum := @rownum + 1) as num, b.category_name, b.title, b.board_group, b.board_sort, b.board_depth, (select count(board_id) from comments where comments.board_id = b.id and comments.del_yn = 'N') as comments_count, (select count(board_id) from upload_file where upload_file.board_id = b.id) as file_count, b.create_time, b.update_time, b.del_yn
FROM board as b left join comments c on b.id = c.board_id, (select @rownum := 0) as num_table WHERE b.del_yn = 'N' and b.category_code = #{categoryCode} group by b.id order by board_group desc, board_sort asc, num desc
limit #{paginationInfo.firstRecordIndex}, #{recordsPerPage};

SELECT
        IFNULL(MIN(board_sort),0)
FROM
        board
WHERE
      board_group = ${boardGroup}
  AND board_sort > ${boardSort}
  AND board_depth  <=  ${boardDepth}
  AND del_yn = 'N';

mapper.update:
UPDATE
    board
SET
    board_sort = board_sort + 1
WHERE

    board_group =  #{boardGroup}
  AND board_sort  >=   #{boardSort}
  AND del_yn = 'N';
