mapper.select:
select id , original_file_name , store_file_name , extension_name , store_file_size , store_file_path , create_time , board_id
from board_file
where board_id = #{id}

select b.id , ba.category_name , ba.category_number , b.title , b.contents , b.create_time , b.update_time , b.del_yn , b.board_admin_id , uf.board_id
from board as b inner join board_admin as ba on ba.id = b.board_admin_id inner join board_file uf on b.id = uf.board_id
where b.del_yn = 'N' and ba.id = #{id};

mapper.insert:
insert into board(
                  title
                  , contents
                  , board_group
                  , board_sort
                  , board_depth
                  , create_time
                  , update_time
                  , board_admin_id
)values(
                    #{title}
                    , #{contents}
                    , #{boardGroup}
                    , #{boardSort}
                    , #{boardDepth}
                    , #{createTime}
                    , #{updateTime}
                    , #{boardAdminId}
)

mapper.update:
update board
set
    title = #{title}
    , contents = #{contents}
    , update_time = #{updateTime}

where
      del_yn = 'N' and id = #{id}

mapper.select:
select ifnull(min(board_sort), 0)

from    board

where
          board_group = ${boardGroup}
      and board_sort > ${boardSort}
      and board_depth  <=  ${boardDepth}
      and del_yn = 'N';

select ifnull(max(board_sort), 0) + 1
from board
where board_group = ${boardGroup} and del_yn = 'N';

mapper.insert:
insert into board(
                    title
                    , contents
                    , board_group
                    , board_sort
                    , board_depth
                    , create_time
                    , update_time
                    , board_admin_id

)values(
                   #{title}
                   , #{contents}
                   , #{boardGroup}
                   , #{boardSort}
                   , #{boardDepth} + 1
                   , #{createTime}
                   , #{updateTime}
                   , #{boardAdminId}
       )

mapper.update:
update  board
set     board_sort = board_sort + 1
where
        board_group =  #{boardGroup}
      and board_sort  >=   #{boardSort}
      and del_yn = 'N';

update  board
set     board_sort = board_sort - 1
where   board_group = ${boardGroup} and board_sort > ${boardSort}
