mapper.update:
update
    comments
set
    sort = #{sort} + 1
where comment_group = #{commentGroup}
  and sort > #{sort}
  and board_id = ${boardId}

mapper.select:
SELECT
        max(sort)
FROM
        comments
WHERE
        board_id  = ${boardId}
        AND comment_group = #{commentGroup}
        AND comment_depth = #{commentDepth}
        AND del_yn = 'N'

mapper.update:
UPDATE comments
SET contents = #{contents}, update_time = #{updateTime}
WHERE id = #{id} AND del_yn = 'N'

mapper.insert:
UPDATE
    comments
set comment_group = #{id}
where
    board_id = #{boardId}
  AND id = #{id}
  AND del_yn = 'N';

mapper.select:
SELECT
       IFNULL(MIN(sort),0)
FROM
        comments
WHERE
        comment_group = ${commentGroup}
AND sort > ${sort}
AND comment_depth <= ${commentDepth};
